import React from "react";
import numeral from "numeral";
import { Circle, Popup } from "react-leaflet";

//dictionary{object} to represnt different color circles
const casesTypeColors = {
    cases: {
      hex: "#CC1034",
      multiplier: 800,
    },
    recovered: {
      hex: "#7dd71d",
      multiplier: 1200,
    },
    deaths: {
      hex: "#fb4443",
      multiplier: 2000,
    },
};

// not for component but just for some utilities function or i can say helper function
export const sortData = (data) => {
    const sortedData = [...data];

    sortedData.sort((a, b) => (a.cases > b.cases ? -1 : 1));
    return sortedData;
};

//draw circle on the map with interactive tooltip
export const showDataOnMap = (data, casesType='cases') => 
    data.map(country => (
        <Circle
            center={[country.countryInfo.lat, country.countryInfo.lng]}
            fillOpacity={0.4}
            color={casesTypeColors[casesType].hex}
            fillColor={casesTypeColors[casesType].hex}
            radius={
                Math.sqrt(country[casesType]) * casesTypeColors[casesType].multiplier
            }
        >
            <Popup>Popup</Popup>
        </Circle>
    ));
